import React from "react";

//import {populateResults} from "./SearchBox";
import SearchStore from "../../SearchStore/SearchStore";


export default class Book extends React.Component {
  constructor() {
    super();
    this.getBooks= this.getBooks.bind(this);
    this.state = {
      Books: SearchStore.getAll(),
    };
  }

  componentWillMount() {
    SearchStore.on("change", this.getBooks);
  }

  componentWillUnmount() {
    SearchStore.removeListener("change", this.getBooks);
  }

  getBooks() {
    this.setState({
      Books: SearchStore.getAll(),
    });
  }


  render() {
    const { Books} = this.state;

    const BookComponents = Books.map((book) => {
        return <Book key={book.id} {...book}/>;
    });

    return (
      <div>
        <h1>Books</h1>
        <ul>{BookComponents}</ul>
      </div>
    );
  }
}
