import axios from 'axios'
var parseString = require('xml2js').parseString;

const bookSearch = (query) =>{
    axios.get('https://www.goodreads.com/search/index.xml?key=rgMebNe9PaPTpob7TImEw&q=' + query, { crossdomain: true })
    .then(function (response) {
      //console.log(response);
      parseString(response.data, {trim: true}, function (err, convertedResult) {
        if (err) throw err
        console.log(convertedResult.GoodreadsResponse.search[0]);
        return convertedResult.GoodreadsResponse.search[0]
      });
    })
    .catch(function (error) {
      console.log(error);
    })
}

export default bookSearch
