import React from "react";
import axios from 'axios'
var parseString = require('xml2js').parseString;
//import { Redirect } from 'react-router-dom';


const WAIT_INTERVAL = 1000
let typingTimer = ''

export default class SearchBox extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      navigate: false,
      referrer: null,
    };
  }

  // handleClick = () => {
  //   console.log('Button is cliked!');
  //   this.setState({ referrer: '/Search' });
  // }

  handleChange = (e) => {
    clearTimeout(typingTimer);
    typingTimer = setTimeout(doneTyping, WAIT_INTERVAL);

  }

  render() {
    const inputStyles = {
      marginTop: "30px",
    };

    // const {referrer} = this.state;
    // if (referrer) return <Redirect to={referrer} />;

    //onClick={this.handleClick}

    return (
      <div>
        <input type="text" onChange={this.handleChange} style={inputStyles} />
        <button onClick={this.handleClick}>Search</button>
      </div>
    );
  }
}

const doneTyping = (e) => {
  const query = e.target.value
  bookSearch(query)
}

const bookSearch = (query) =>{
  axios.get('https://www.goodreads.com/search/index.xml?key=rgMebNe9PaPTpob7TImEw&q=' + query, { crossdomain: true })   //
  .then(function (response) {
    // handle success
    console.log(response);
    parseString(response.data, {trim: true}, function (err, convertedResult) {
      if (err) throw err
      console.log(convertedResult.GoodreadsResponse.search[0]);
    });
  })
  .catch(function (error) {
    // handle error
    console.log(error);
  })
}
